tasks.command:
echo "/* Mystic Emergence Animation */
@keyframes mystic-emerge {
  0% {
    opacity: 0;
    transform: translateY(10px) scale(0.98);
    filter: blur(4px);
  }
  100% {
    opacity: 1;
    transform: translateY(0) scale(1);
    filter: blur(0);
  }
}

.animate-mystic-emerge {
  animation: mystic-emerge 1.2s cubic-bezier(0.22, 1, 0.36, 1) forwards;
}" >> frontend/src/index.css
